import React from 'react';
import { Link } from 'react-router-dom';

class Gallery extends React.Component {
	componentDidMount() {
		document.querySelector('body').className = 'gallery';
	}
	componentWillUnmount() {
		document.querySelector('body').className = '';
	}
	render() {
		return (
			<>
				<div>
					{/* <section> */}
					<div className="image-wrapper content-padding">
						<img
							className="image"
							src={`${process.env.PUBLIC_URL}/images/media_2.jpg`}
							alt="sample image"
						/>
						<h1 className="main-title">Media</h1>
					</div>
					<h1>
						<span>Media</span>
					</h1>
					<ul class="gallery">
						<li>
							<Link to="/music">
								<img src="images/gallery/Music.jpg" alt="" />
							</Link>
						</li>
						<li>
							<Link to="/video">
								<img src="images/gallery/Video.jpg" alt="" />
							</Link>
						</li>
						<li>
							<Link to="/lyrics">
								<img src="images/gallery/Lyrics.jpg" alt="" />
							</Link>
						</li>

						<li>
							<Link to="/lyrics">
								<img src="images/gallery/Lyrics.jpg" alt="" />
							</Link>
						</li>
					</ul>
				</div>
			</>
		);
	}
}

export default Gallery;
