import React from 'react';
import { connect } from 'react-redux';
import { fetchPhotos, fetchVideos, fetchLyrics } from '../actions';

class Gallery extends React.Component {
	componentDidMount() {
		document.querySelector('body').className = 'gallery';
		this.props.fetchPhotos();
		this.props.fetchVideos();
		this.props.fetchLyrics();
	}
	componentWillUnmount() {
		document.querySelector('body').className = '';
	}

	renderMediaContents(selectedMedia) {
		if (selectedMedia === 'photos') {
			return this.props.photos.map(photo => {
				return (
					<div class="ui card">
						<div class="image">
							<img src="/images/avatar2/large/kristy.png" />
						</div>
						<div class="content">
							<a class="header">Kristy</a>
							<div class="meta">
								<span class="date">Joined in 2013</span>
							</div>
							<div class="description">
								Kristy is an art director living in New York.
							</div>
						</div>
						<div class="extra content">
							<a>
								<i class="user icon" />
								22 Friends
							</a>
						</div>
					</div>
				);
			});
		}
	}

	render() {
		console.log(this.props.photos);
		return (
			<>
				<div>
					{/* <section> */}
					<div className="image-wrapper content-padding">
						<img
							className="image"
							src={`${process.env.PUBLIC_URL}/images/photographer.jpg`}
							alt="sample image"
						/>
						<h1 className="main-title">Media</h1>
					</div>
					<h1>
						<span>Media</span>
					</h1>
					<ul class="gallery">
						<li>
							<button>Photos</button>
						</li>
						<li>
							<button>Videos</button>
						</li>
						<li>
							<button>Lyrics</button>
						</li>

						<li>
							<button>Other</button>
						</li>
					</ul>
				</div>
			</>
		);
	}
}

const mapToStateProps = state => {
	return {
		photos: Object.values(state.photos),
		videos: Object.values(state.videos),
		lyrics: Object.values(state.lyrics)
	};
};

export default connect(
	mapToStateProps,
	{
		fetchPhotos,
		fetchVideos,
		fetchLyrics
	}
)(Gallery);
