import React from 'react';
import { connect } from 'react-redux';
import ShowGallery from './ShowGallery';

class Gallery extends React.Component {
	componentDidMount() {
		document.querySelector('body').className = 'gallery';
	}
	componentWillUnmount() {
		document.querySelector('body').className = '';
	}

	renderMedias = () => {
		return this.state.medias.map(media => {
			<li className="gallery-media">
				<button className="ui button primary">Photos</button>
			</li>;
		});
	};

	render() {
		console.log(this.state.selectedMedia);
		return (
			<>
				<div>
					{/* <section> */}
					<div className="image-wrapper content-padding">
						<img
							className="image"
							src={`${process.env.PUBLIC_URL}/images/photographer.jpg`}
							alt="sample image"
						/>
						<h2 className="main-title">Gallery</h2>
					</div>
					<h2>
						<span>Media</span>
					</h2>
					<ul className="gallery">
						<li className="gallery-media">
							<button
								onClick={() => this.renderMedia('photo')}
								className="ui button primary"
							>
								Photos
							</button>
						</li>
						<li className="gallery-media">
							<button
								onClick={() => this.renderMedia('video')}
								className="ui button primary"
							>
								Videos
							</button>
						</li>
						<li className="gallery-media">
							<button
								onClick={() => this.renderMedia('lyric')}
								className="ui button primary"
							>
								Lyrics
							</button>
						</li>

						<li className="gallery-media">
							<button
								onClick={() => this.renderMedia('other')}
								className="ui button primary"
							>
								Other
							</button>
						</li>
					</ul>
					<ShowGallery selectedMedia={this.state.selectedMedia} />
				</div>
			</>
		);
	}
}

const mapToStateProps = state => {
	console.log(state.medias);
	return {
		medias: state.medias
	};
};

export default connect(mapToStateProps)(Gallery);
