import { SELECT_MEDIA } from '../actions/type';

export const mediasReducer = () => {
	return [
		{
			media: 'Photo',
			img: `${process.env.PUBLIC_URL}/images/photographer.jpg`
		},
		{
			media: 'Video',
			img: `${process.env.PUBLIC_URL}/images/photographer.jpg`
		},
		{
			media: 'Lyric',
			img: `${process.env.PUBLIC_URL}/images/photographer.jpg`
		},
		{ media: 'Other', img: `${process.env.PUBLIC_URL}/images/photographer.jpg` }
	];
};

export const selectedMediaReducer = (selectedMedia = null, action) => {
	if (action.type === SELECT_MEDIA) {
		return action.payload;
	}
	return selectedMedia;
};
