import React from 'react';
import { connect } from 'react-redux';
import { fetchPhotos, fetchVideos, fetchLyrics } from '../actions';
import ShowGallery from './ShowGallery';

class Gallery extends React.Component {
	state = { selectedMedia: '' };

	componentDidMount() {
		document.querySelector('body').className = 'gallery';
		this.props.fetchPhotos();
		this.props.fetchVideos();
		this.props.fetchLyrics();
	}
	componentWillUnmount() {
		document.querySelector('body').className = '';
	}

	renderPhotos = () => {
		// console.log(this.props.photos);
		if (this.props == null) {
			return <div>Loading</div>;
		}
		return this.props.photos.map(photo => {
			// console.log(photo);
			return (
				<div key={photo.id} className="ui card">
					<div className="image">
						<img src={photo.acf.photo.url} />
					</div>
					<div className="content">
						<div className="header">{photo.acf.description}</div>
					</div>
				</div>
			);
		});
	};

	renderMedia = selectedMedia => {
		console.log(selectedMedia);
		if (selectedMedia === 'photo') {
			this.setState({ selectedMedia: selectedMedia });
		}
	};

	render() {
		console.log(this.state.selectedMedia);
		return (
			<>
				<div>
					{/* <section> */}
					<div className="image-wrapper content-padding">
						<img
							className="image"
							src={`${process.env.PUBLIC_URL}/images/photographer.jpg`}
							alt="sample image"
						/>
						<h2 className="main-title">Gallery</h2>
					</div>
					<h2>
						<span>Media</span>
					</h2>
					<ul className="gallery">
						<li className="gallery-media">
							<button
								onClick={() => this.renderMedia('photo')}
								className="ui button primary"
							>
								Photos
							</button>
						</li>
						<li className="gallery-media">
							<button
								onClick={() => this.renderMedia('video')}
								className="ui button primary"
							>
								Videos
							</button>
						</li>
						<li className="gallery-media">
							<button
								onClick={() => this.renderMedia('lyric')}
								className="ui button primary"
							>
								Lyrics
							</button>
						</li>

						<li className="gallery-media">
							<button
								onClick={() => this.renderMedia('other')}
								className="ui button primary"
							>
								Other
							</button>
						</li>
					</ul>
					<ShowGallery selectedMedia={this.state.selectedMedia} />
				</div>
			</>
		);
	}
}

const mapToStateProps = state => {
	// console.log(state.photos);
	return {
		photos: Object.values(state.photos),
		videos: Object.values(state.videos),
		lyrics: Object.values(state.lyrics)
	};
};

export default connect(
	mapToStateProps,
	{
		fetchPhotos,
		fetchVideos,
		fetchLyrics
	}
)(Gallery);
